import Navbar from "./Navbar"
import "../EventsCreatePage.css"
import {useState} from "react"
import axios from "axios"

export default function EventsCreatePage(){
    const [eventFormInfo, setEventFormInfo] = useState({eventName:"",eventDescription:"",eventTime:"",eventDate:"",eventSlots:0})

    function handleDrop(){

    }
    function createEventFormSubmit(e){
        e.preventDefault()
        axios.post("http://localhost:5000/eventsinfo/add", eventFormInfo)
        .then(res => console.log(res.data))
        window.location.reload()
    }
    function handleEventsFormChange(e){
        setEventFormInfo(prev =>({
            ...prev,
            [e.target.name]: e.target.value
        }))
    }

    console.log(eventFormInfo)
    return(
        <div>
            <Navbar />
            <form onSubmit={createEventFormSubmit} className="create-events-form">
                <input required onChange={handleEventsFormChange} name="eventName" className="create-events-input" type="text" placeholder="Title"></input>
                <input required onChange={handleEventsFormChange} name="eventDescription" className="create-events-input" type="text" placeholder="Description" />
                <input onChange={handleEventsFormChange} name="eventSlots" className="create-events-input" type="number" placeholder="Slots" />
                <input required onChange={handleEventsFormChange} name="eventTime" className="create-events-input" type="text" placeholder="Time" />
                <div className="form-bottom-row">
                    <div className="img-drop" onDrop={handleDrop}>
                    <svg className="img-drop-icon" xmlns="http://www.w3.org/2000/svg" width="50" height="43" viewBox="0 0 50 43">
                        <path d="M48.4 26.5c-.9 0-1.7.7-1.7 
                        1.7v11.6h-43.3v-11.6c0-.9-.7-1.7-1.7-1.7s-1.7.7-1.7 1.7v13.2c0 .9.7 1.7 1.7 1.7h46.7c.9 0 1.7-.7 1.7-1.7v-13.2c0-1-.7-1.7-1.7-1.7zm-24.5 
                        6.1c.3.3.8.5 1.2.5.4 0 .9-.2 1.2-.5l10-11.6c.7-.7.7-1.7 0-2.4s-1.7-.7-2.4 0l-7.1 8.3v-25.3c0-.9-.7-1.7-1.7-1.7s-1.7.7-1.7 1.7v25.3l-7.1-8.3c-.7-.7-1.7-.7-2.4 0s-.7 1.7 0 2.4l10 11.6z"></path>
                        </svg>
                        <p>Drag a file</p>
                    </div>
                    <button className="events-form-submit" type="submit">Submit Event</button>
                </div>
            </form>
        </div>
    )
}

