import Navbar from "./Navbar"
import "../EventsCreatePage.css"
import {useState} from "react"
import axios from "axios"

export default function EventsCreatePage(){
    const [eventFormInfo, setEventFormInfo] = useState({eventName:"",eventDescription:"",eventDate:"",eventSlots:0})

    function handleDrop(){

    }
    function createEventFormSubmit(e){
        e.preventDefault()
        axios.post("http://localhost:5000/eventsinfo/add", eventFormInfo)
        .then(res => console.log(res.data))
        window.location.reload()
    }
    function handleEventsFormChange(e){
        setEventFormInfo(prev =>({
            ...prev,
            [e.target.name]: e.target.value
        }))
    }

    console.log(eventFormInfo)
    return(
        <div>
            <Navbar />
            <form onSubmit={createEventFormSubmit} className="create-events-form">
                <input onChange={handleEventsFormChange} name="eventName" className="create-events-input" type="text" placeholder="Title"></input>
                <input onChange={handleEventsFormChange} name="eventDescription" className="create-events-input" type="text" placeholder="Description" />
                <input onChange={handleEventsFormChange} name="eventDate" className="create-events-input" type="text" placeholder="Date" />
                <input onChange={handleEventsFormChange} name="eventSlots" className="create-events-input" type="number" placeholder="Slots" />
                <div className="form-bottom-row">
                    <div className="img-drop" onDrop={handleDrop}>Drag Image</div>
                    <button className="eventsform-submit" type="submit">Submit Event</button>
                </div>
            </form>
        </div>
    )
}
